import React from 'react';
import styles from './AboutSection.module.css';

const AboutSection = () => {
  return (
    <section className={styles.about} data-testid="about">
      {/* Textual content */}
      <div className={styles.left}>
        <h2 className={styles.title}>Being Human in the Age of AI</h2>
        <p className={styles.description}>
          To wyjątkowe wydarzenie, na które zapraszają Uniwersytet Jagielloński oraz This Is It. 
          Gromadzi ono największe umysły sztucznej inteligencji z Polski, w tym Wojciecha Zarembę, 
          Andrzeja Dragana, współzałożycieli Eleven Labs i Iceye oraz wiodących polskich akademików.
        </p>
        <p className={styles.invitation}>
          Serdecznie zapraszamy na to wydarzenie laureatów i finalistów polskich olimpiad 
          przedmiotowych z przedmiotów ścisłych.
        </p>
        <button className={styles.button}>Formularz zgłoszeniowy</button>
      </div>

      {/* Interview photo */}
      <div className={styles.right}>
        <a
          href="https://www.youtube.com/watch?v=your_video_id"
          target="_blank"
          rel="noopener noreferrer"
          className={styles.photoLink}
        >
          <img
            src="/wywiad.png"
            alt="Wywiad Macieja Kaweckiego z Wojciechem Zarembą"
            className={styles.photo}
          />
        </a>
        <p className={styles.caption}>
          Wywiad Macieja Kaweckiego z Wojciechem Zarembą
        </p>
      </div>
    </section>
  );
};

export default AboutSection;
